mapper.select:
SELECT *
FROM genFile
WHERE 1

	AND relId = #{relId}


	AND fileNo = #{fileNo}

mapper.update:
UPDATE genFile

	updateDate = NOW(),
	relId = #{relId}

WHERE id = #{id}

UPDATE genFile
delDate = NOW(), delStatus = 1, fileDir = "deleteFolder"
WHERE id = #{id}

UPDATE genFile

	updateDate = NOW(),
	delDate = null,
	delStatus = 0,
	fileDir = #{fileDir}

WHERE id = #{id}
